sources.styles/page.scss:
.page {
  max-width: 1280px;
  margin: auto;
  position: relative;
  z-index: -1;

  @include onTablet {
    max-width: 768px;
  }

  @include onPhone {
    width: 100%;
  }

  &__backdrop {
    position: fixed;
    max-width: 100%;
    margin: auto;
    min-height: 100%;
    background-color: $backdrop-color;
    z-index: 1;
    color: $backdrop-text-color;
    left: 0;
    right: 0;

    @include onTablet {
      max-width: 100%;
    }

    @include onPhone {
      width: 100%;
      overflow: auto;
    }
  }

  &__menu-content {
    padding: 40px 155px 32px 182px;
    gap: 102px;
    position: relative;
    max-width: 1280px;
    margin: auto;
    display: flex;

    @include onTablet {
      max-width: 768px;
      padding: 40px 35px 40px 96px;
      gap: 38px;
    }

    @include onPhone {
      width: 100%;
      padding: 20px 27px;
      display: block;

    }
  }

  &__menu {
    background: $menu-color;
    z-index: 1;
  }

  &__close {
    position: absolute;
    left: 56px;
    top: 40px;
    display: block;
    width: 24px;
    height: 24px;
    background: url(../images/close.png) no-repeat center;

    transition: transform 0.3s ease;
    transform-origin: center;

    &:hover {
      transform: scale(1.2);

      @include onTablet {
        transform: none;
      }

      @include onPhone {
        transform: none;
      }
    }

    @include onTablet {
      left: 40px;
      top: 45px;
    }

    @include onPhone {
      left: 25px;
      top: 32px;
    }
  }

  &_menu-puncts {
    display: flex;
    gap: 30px;
    font-weight: 500;
    font-size: 22px;
    line-height: 100%;

    @include onTablet {
      gap: 20px;
      font-size: 16px;

    }

    @include onPhone {
      gap: 28px;
      font-size: 16px;
      margin: auto;
      padding: 27px 0 24px 72px;
    }
  }

  &__link {
    display: block;
    color: $backdrop-text-color;
    width: 145px;
    &:hover {
      text-decoration: underline;

      @include onTablet {
        text-decoration: none;
      }

      @include onPhone {
        text-decoration: none;
      }
    }

    @include onTablet {
      width: 100px;
    }

    @include onPhone {
      width: 100px;
    }
  }

  &__line {
    height: 145px;
    margin: 0;

    @include onPhone {
      height: 0;
      width: 100%;
      margin: 0;
    }
  }

  &__items {
    display: flex;
    flex-direction: column;
    gap: 30px;
    width: 168px;

    @include onTablet {
      gap: 24px;
      width: 100px;
    }

    @include onPhone {
      gap: 24px;
      width: 100px;
    }
  }

  &__paragraphs {
    display: flex;
    gap: 30px;
    font-weight: 600;
    font-size: 14px;
    line-height: 150%;

    @include onTablet {
      gap: 38px;
    }

    @include onPhone {
      gap: 16px;
      width: 280px;
      flex-direction: column;
    }
  }

  &__graph {
    font-weight: 400;
    font-size: 14px;
    line-height: 140.62%;
    margin-bottom: 20px;

    @include onPhone {
      line-height: 150%;
      margin-bottom: 16px;
    }
  }

  &__time-work {
    width: 170px;

    @include onTablet {
      width: 159px;
    }

  }

  &__holiday {
    width: 170px;

    @include onTablet {
      width: 159px;
    }

    @include onPhone {
      width: 280px;
    }
  }

  &__right {
    @include onPhone {
      padding-top: 24px;
    }
  }
}

#backdrop {
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  opacity: 0;
  visibility: hidden;
  transition: opacity 0.3s ease, visibility 0.3s ease;
}

#backdrop:target {
  opacity: 1;
  visibility: visible;
}

sources.styles/header.scss:
.header {
  position: relative;
  display: flex;
  flex-direction: row-reverse;
  gap: 30px;
  padding-left: 55px ;
  margin-bottom: 200px;

  @include onTablet {
    padding-left: 34px ;
    gap: 20px;
  }

  @include onPhone {
    flex-direction: column;
    width: 100%;
    padding-left: 0;
    gap: 20px;
    margin-bottom: 70px;
  }

  &_background {
    width: 725px;

    @include onTablet {
      width: 50%;
    }

    @include onPhone {
      width: 100%;
    }
  }

  &__link {
    position: absolute;
    top: 15px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    justify-content: center;
    align-items: center;
    width: 170px;
    height: 72px;

    @include onTablet {
      transform: translateX(0);
      width: 220px;
    }

    @include onPhone {
      top: 9px;
      width: 55px;
      height: 61px;
    }
  }

  &__open {
    position: absolute;
    left: 56px;
    top: 40px;
    display: block;
    width: 24px;
    height: 24px;
    background: url(../images/burger-menu.png) no-repeat center;

    transition: transform 0.3s ease;
    transform-origin: center;

    &:hover {
      transform: scale(1.2);

      @include onTablet {
        transform: none;
      }
      @include onPhone {
        transform: none;
      }
    }

    @include onTablet {
      left: 37px;
      top: 45px;
    }

    @include onPhone {
      left: 23px;
      top: 33px;
    }
  }

  &__title {
    font-weight: 700;
    font-size: 72px;
    line-height: 120%;
    padding: 205px 0 90px;

    @include onTablet {
      font-size: 54px;
      padding: 127px 0 38px;
    }

    @include onPhone {
      padding: 0;
      font-size: 45px;
      line-height: 100%;
      margin-bottom: 20px;
      text-align: center;
    }
  }

  &__block {
    @include onPhone {
      padding: 0 20px;
    }
  }

  &_ticket {
    display: flex;
    padding-left: 200px;

    @include onTablet {
      padding-left: 0;
      width: 270px;
    }

    @include onPhone {
      padding: 0;
      font-size: 45px;
      line-height: 100%;
    }
  }

  &__button {
    display: block;
    width: 70px;
    height: 70px;
    background-image: url(../images/arrow-white.png);
    background-repeat: no-repeat;
    background-position: center center;
    background-color: $menu-color;
    transition: all 0.3s ease;

    &:hover {
      background: url(../images/arrow-black.png) transparent no-repeat center;
      border: 1px solid $menu-color;

      @include onTablet {
        background: none;
        border: none;
      }

      @include onPhone {
        background: none;
        border: none;
      }
    }

    @include onPhone {
      width: 50px;
      height: 50px;
    }
  }

  &__tickets {
    height: 70px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-grow: 1;
    border: 1px solid $menu-color;
    font-weight: 500;
    font-size: 16px;
    line-height: 100%;

    @include onPhone {
      height: 50px;
    }
  }
}

sources.styles/events.scss:
.events {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 56px;
  margin-bottom: 104px;

  @include onTablet {
    gap: 48px;
  }

  @include onPhone {
    gap: 40px;
    margin-bottom: 60px;
  }

  &__before {
    position: absolute;
    display: flex;
    align-items: center;
    transform: rotate(270deg);
    left: -60px;
    top: -260px;
    color: #687480;
    font-weight: 700;
    font-size: 14px;

    @include onTablet {
      display: none;
    }

    @include onPhone {
      display: none;
    }

    &::before {
      content: "";
      display: inline-block;
      border-top: 1px solid $menu-color;
      width: 70px;
      margin-right: 20px;
    }
  }

  &__conteiner {
    display: flex;
    flex-direction: column;
    gap: 104px;

    @include onTablet {
      gap: 64px;
    }

    @include onPhone {
      gap: 60px;
    }
  }

  &__block {
    display: flex;
    justify-content: space-between;
    align-items: center;

    @include onTablet {
      flex-direction: column;
      align-items: initial;
    }

    @include onPhone {
