    @include onTablet {
      width: 570px;
      height: 204px;
      top: 306px;
      padding: 45px 55px 50px;
    }

    @include onPhone {
      min-width: 90%;
      padding: 20px 0;
      text-align: center;
      left: 50%;
      top: 100%;
      transform: translateY(-35%) translateX(-50%);
    }
  }
