      cursor: pointer;

      @include onTablet {
        border: 1px solid #f14a27;
      }
    }

    @include onPhone {
      height: 50px;
      width: 50px;
      border: 1px solid #f14a27;
    }
  }
}

sources.styles/footer.scss:
.footer {
  display: flex;
  font-weight: 400;
  font-size: 14px;
  line-height: 100%;
  margin-bottom: 87px;

  @include onTablet {
    margin-bottom: 120px;
    flex-direction: column;
  }

  @include onPhone {
    margin-bottom: 80px;
    flex-direction: column;
  }

  &__top {
    display: flex;

    @include onPhone {
      display: block;
    }
  }

  &__icons {
    display: flex;
    flex-direction: column;
    gap: 62px;

    @include onPhone {
      flex-direction: row;
      justify-content: space-between;
      margin-bottom: 40px;
    }
  }

  &__socials {
    display: flex;
    gap: 28px;
  }

  &__social {
    width: 24px;
    height: 24px;
    transition: transform 0.6s ease;
    transform-origin: center;

    &:hover {
      transform: scale(1.2);

      @include onTablet {
        transform: none;
      }

      @include onPhone {
        transform: none;
      }
    }
  }

  &__graph {
    width: 170px;

    @include onTablet {
      width: 220px;
    }

    @include onPhone {
      width: 130px;
    }
  }

  &__title {
    padding-bottom: 20px;
    font-weight: 400;
  }

  &__time-work {
    padding-bottom: 20px;
  }

  &__contacts {
    width: 170px;
    font-weight: 400;

    @include onTablet {
      width: 220px;
    }
    @include onPhone {
      width: 130px;
    }
  }

  &__data {
    display: flex;
    font-weight: 600;
    line-height: 150%;
    padding: 0 65px 0 130px;
    gap: 30px;

    @include onTablet {
      padding-left: 40px;
      margin-bottom: 64px;
    }

    @include onPhone {
      padding: 0;
      justify-content: space-between;
      font-weight: 400;
      font-size: 14px;
    }
  }

  &__line {
    height: 260px;
    margin: 0;
    background-color: $menu-color;

    @include onTablet {
      transform: rotateY(180deg);
      height: 0;
      width: 460px;
      margin: auto;
    }

    @include onPhone {
      height: 0;
      width: 100%;
      margin: 70px 0 40px;
    }
  }

  &__links {
    display: flex;
    font-weight: 400;
    gap: 32px;
    padding-left: 65px;

    @include onTablet {
      padding-top: 64px;
      margin: auto;
    }

    @include onPhone {
      padding-left: 0;
    }
  }

  &__holiday {
    font-weight: 600;
  }

  &__link {
    &:hover {
      text-decoration: underline;

      @include onTablet {
        text-decoration: none;
      }
    }
  }

  &__items {
    display: flex;
    flex-direction: column;
    gap: 20px;
    width: 170px;

    @include onTablet {
      width: 220px;
    }
  }

  &__captcha {
    display: flex;
    justify-content: space-between;
    font-weight: 300;
    font-size: 12px;
    line-height: 140.62%;
    color: #0f0e08;
    padding-bottom: 35px;

    @include onTablet {
      padding: 0 35px 35px;
    }

    @include onPhone {
      flex-direction: column-reverse;
      gap: 10px;
      align-items: center;
    }
  }

  &__circle {

    @include onPhone {
      position: absolute;
      bottom: 280px;
      right: 20px;
      display: block;
      width: 50px;
      height: 50px;
      border: 1px solid $menu-color;
      border-radius: 50%;
      background: url(../images/arrow-top.png) center no-repeat;
    }
  }
}
